import React, { useEffect, useState } from "react";
import axios from "axios";
import HomeSection from "../HomeSection";
import SearchBar from "../SearchBar";
import Case from "../Case";
import { CircularProgress } from "@material-ui/core";
import Alert from "@material-ui/lab/Alert";

function Home() {
  const [results, setResults] = useState([]);
  const [errMsg, setErrMsg] = useState("hello");
  //api call here
  const onSearchSubmit = async (term, fromDate, toDate) => {
    console.log(term, "here");
    const res = await axios
      .get("https://bikewise.org:443/api/v2/incidents", {
        params: {
          proximity: "Berlin",
          proximity_square: 100,
          query: term,
          per_page: 100,
          occurred_after: fromDate,
          occurred_before: toDate,
        },
      })
      .catch((err) => setErrMsg(err.message));
    setResults(res.data.incidents);
    console.log(res, "result");
    console.log(res.data.incidents.length);
  };

  useEffect(() => {
    onSearchSubmit("");
  }, []);

  return (
    <>
      <HomeSection />
      <SearchBar onSearchSubmit={onSearchSubmit} />
      {!errMsg && <Alert severity="error">{errMsg}</Alert>}
      {!results.length && (
        <CircularProgress
          size={200}
          thickness={2}
          style={{ marginLeft: "42%" }}
        />
      )}
      {results.length && <Case results={results} />}
    </>
  );
}

export default Home;
